mapper.select:
SELECT


,changdi.changdi_name as changdiName
,changdi.changdi_photo as changdiPhoto
,changdi.changdi_types as changdiTypes
,changdi.changdi_kucun_number as changdiKucunNumber
,changdi.changdi_new_money as changdiNewMoney
,changdi.changdi_clicknum as changdiClicknum
,changdi.changdi_content as changdiContent
,changdi.shangxia_types as shangxiaTypes
,changdi.data_delete as dataDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney
,yonghu.data_delete as dataDelete

FROM changdi_yuyue  a
left JOIN changdi changdi ON a.changdi_id = changdi.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and a.changdi_yuyue_uuid_number like CONCAT('%',#{params.changdiYuyueUuidNumber},'%')


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and (
            a.changdi_id = #{params.changdiId}
        )


        and a.sheshiyuyue_text like CONCAT('%',#{params.sheshiyuyueText},'%')


          and UNIX_TIMESTAMP(a.changdi_yuyue_time) >= UNIX_TIMESTAMP(#{params.changdiYuyueTimeStart}) 


          and UNIX_TIMESTAMP(a.changdi_yuyue_time) <= UNIX_TIMESTAMP(#{params.changdiYuyueTimeEnd}) 


        and a.changdi_yuyue_time = #{params.changdiYuyueTime}


          and UNIX_TIMESTAMP(a.changdi_jieshu_time) >= UNIX_TIMESTAMP(#{params.changdiJieshuTimeStart}) 


          and UNIX_TIMESTAMP(a.changdi_jieshu_time) <= UNIX_TIMESTAMP(#{params.changdiJieshuTimeEnd}) 


        and a.changdi_jieshu_time = #{params.changdiJieshuTime}


        and a.changdi_yuyue_yesno_types = #{params.changdiYuyueYesnoTypes}


        and a.changdi_yuyue_yesno_text like CONCAT('%',#{params.changdiYuyueYesnoText},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.changdi_id IS NOT NULL


        and changdi.changdi_name like CONCAT('%',#{params.changdiName},'%')


        and changdi.changdi_types = #{params.changdiTypes}



          and changdi.changdi_kucun_number >= #{params.changdiKucunNumberStart}   


          and changdi.changdi_kucun_number <= #{params.changdiKucunNumberEnd}   


        and changdi.changdi_kucun_number = #{params.changdiKucunNumber}


          and changdi.changdi_new_money >= #{params.changdiNewMoneyStart}   


          and changdi.changdi_new_money <= #{params.changdiNewMoneyEnd}   


        and a.changdi_new_money = #{params.changdiNewMoney}


          and changdi.changdi_clicknum >= #{params.changdiClicknumStart}   


          and changdi.changdi_clicknum <= #{params.changdiClicknumEnd}   


        and changdi.changdi_clicknum = #{params.changdiClicknum}


        and changdi.changdi_content like CONCAT('%',#{params.changdiContent},'%')


        and changdi.shangxia_types = #{params.shangxiaTypes}



          and changdi.data_delete >= #{params.dataDeleteStart}   


          and changdi.data_delete <= #{params.dataDeleteEnd}   


        and changdi.data_delete = #{params.dataDelete}


          and UNIX_TIMESTAMP(changdi.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(changdi.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


        and a.new_money = #{params.newMoney}


          and yonghu.data_delete >= #{params.dataDeleteStart}   


          and yonghu.data_delete <= #{params.dataDeleteEnd}   


        and yonghu.data_delete = #{params.dataDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


order by a.${params.sort} ${params.order}
